(49, 'storefront-customize', 'info', 'en_US', 'Design your store with Storefront 🎨', 'Visit the Storefront settings page to start setup and customization of your shop.', '{}', 'unactioned', 'storefront', '2023-07-04 10:26:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'woopay-beta-merchantrecruitment-short-update-09MAY23', 'info', 'en_US', 'Increase conversions with WooPay — our fastest checkout yet', 'Be one of the first to try WooPay, our new express checkout feature. <br>Boost conversions by letting customers pay with a single click. <br><br>Update to the latest version of WooCommerce Payments to get started.', '{}', 'pending', 'woocommerce.com', '2023-07-04 10:26:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'ipp_refresh_q2_2023_us_inbox_notification', 'marketing', 'en_US', 'Grow on the go with in-person payments', 'Sell your products or services on the go with the M2 card reader – a quick, synchronized, and secure way to take payments, no matter where you are. Create an order using the WooCommerce mobile app and connect your card reader to accept payments.', '{}', 'pending', 'woocommerce.com', '2023-07-04 10:26:30', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'ipp_refresh_q2_2023_ca_inbox_notification', 'marketing', 'en_US', 'Grow on the go with in-person payments', 'Sell your products or services on the go with the WisePad 3 card reader – a quick, synchronized, and secure way to take payments, no matter where you are. Create an order using the WooCommerce mobile app and connect your card reader to accept payments.', '{}', 'pending', 'woocommerce.com', '2023-07-04 10:26:30', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'woocommerce-WCstripe-May-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Stripe plugin', '<strong>Your store requires a security update for the WooCommerce Stripe plugin</strong>. Please update the WooCommerce Stripe plugin immediately to address a potential vulnerability.', '{}', 'pending', 'woocommerce.com', '2023-07-04 10:26:30', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'woocommerce-WCPayments-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Payments', '<strong>Your store requires a security update for the WooCommerce Payments plugin</strong>. Please update the WooCommerce Payments plugin immediately to address a potential vulnerability.', '{}', 'pending', 'woocommerce.com', '2023-07-04 10:26:30', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'woocommerce-WCSubscriptions-June-2023-updated-needed', 'marketing', 'en_US', 'Action required: Security update of WooCommerce Subscriptions', '<strong>Your store requires a security update for the WooCommerce Subscriptions plugin</strong>. Please update the WooCommerce Subscriptions plugin immediately to address a potential vulnerability.', '{}', 'pending', 'woocommerce.com', '2023-07-04 10:26:30', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'woocommerce-WCReturnsWarranty-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Returns and Warranty Requests extension', '<strong>Your store requires a security update for the Returns and Warranty Requests extension</strong>.  Please update to the latest version of the WooCommerce Returns and Warranty Requests extension immediately to address a potential vulnerability discovered on May 31.', '{}', 'pending', 'woocommerce.com', '2023-07-04 10:26:30', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'woocommerce_hpos_1st_notification_q2_2023', 'marketing', 'en_US', 'High-Performance Order Storage (HPOS) is on its way', 'Our team is targeting August 2023 to roll out a major database upgrade that can unlock faster checkout and order creation for your customers — and lightning-quick order lookup for you. Enablement is optional and you won’t be forced to update, but read on to see why this was our most requested new feature.', '{}', 'unactioned', 'woocommerce.com', '2023-07-04 10:26:30', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'woocommerce-WCOPC-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce One Page Checkout', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-07-04 10:26:30', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'wc-admin-wcpay-bulgaria-Q2-2023', 'marketing', 'en_US', 'WooCommerce Payments is now available in Bulgaria!', 'We’ve recently released WooCommerce Payments in Bulgaria. You can view and manage transactions right in your WordPress dashboard while securely accepting major cards, Apple Pay, and payments in over 100 currencies.', '{}', 'pending', 'woocommerce.com', '2023-07-04 10:26:30', NULL, 0, 'plain', '', 0, 0, 'info'),
